INSERT INTO `lab_example` VALUES (2,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;font-size:32px;&quot;&gt;&amp;nbsp;于文涛：46 + 30 = 76&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;_shell外壳_____&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;__内核____&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#343541;font-family:-apple-system, blinkmacsystemfont, &amp;quot;font-size:16px;background-color:#F7F7F8;&quot;&gt;系统配置文件&lt;/span&gt;_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_可视___&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/_passwd_____&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_符号__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_fork_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;__top____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;__mkdir____&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;background-color:#E53333;&quot;&gt;_redir_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;_字节_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;___pwd___&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__cd____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;background-color:#E53333;&quot;&gt;gtip_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__gunzip____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;___cat___&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;__echo____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;__./____&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;___&amp;amp;___&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;___|___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;___whereis___&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;__man____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;__ls -l____&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;___ls___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;___find___&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;___grep___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__chmod____&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;__chown____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;___date___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;____ps__&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;___kill___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;__cp____&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;___mv___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_cat&amp;gt;filename__&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;_scp_____&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;_scp_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;__echp____&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;___sed___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;__mount____&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是umount&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;_cat a &amp;gt; b _____&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_grep 关键字 文件路径__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;_passwd_____&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;___id___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep -r____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;___export___&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;___env___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__tar____&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;__tar zxf____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux是一个类Unix操作系统，具有开放源代码的特性。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;1991年，Linus发布了最初版本的Linux内核，它是操作系统的核心组件。随着时间的推移，越来越多的开发者加入进来，贡献代码和改进，使Linux逐渐成为一个强大的、稳定的操作系统。Linux系统也得益于GNU项目，提供了大量的开源工具和应用程序，使得Linux成为一个完整的操作系统。&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 特点： 开源、自由、多样性、可定制性、强大的社区支持、高安全性、稳定性&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;所有者权限、群组权限和其他用户权限。&lt;/span&gt;&amp;nbsp;&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;读取权限（r）、写入权限（w）和执行权限（x）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;权限模式可以使用数字表示（如777）或符号表示（如u+rwx ）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; chmod &amp;lt;权限模式&amp;gt;&amp;lt;文件或目录&amp;gt;&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;模态编辑&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;命令行界面&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;命令行界面&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;正常模式 插入模式 可视模式 命令行模式&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Vim是一款非常强大和灵活的文本编辑器，具有模态编辑、命令行界面和可扩展性等特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; 一个程序可以同时有多个进程在不同的时间运行。在计算机系统中，同一个程序可以被多个用户同时运行，每个用户都会创建一个独立的进程。这些进程相互独立，彼此之间不会相互干扰。&lt;/span&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			创建进程：当用户启动一个程序或系统需要执行一个任务时，操作系统会创建一个新的进程来运行该程序。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程调度：操作系统需要决定在给定时间片内应该运行哪些进程。调度算法决定了进程执行的顺序和时间。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程通信：不同的进程可能需要相互通信和共享数据。操作系统提供了进程间通信（IPC）机制来实现进程之间的信息交换。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程终止：当程序完成任务或出现错误时，进程会被终止并释放占用的资源。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程状态管理：操作系统跟踪每个进程的状态，包括就绪、运行、阻塞等，以便适时地进行调度和资源分配。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程资源管理：操作系统分配和管理进程所需的资源，如内存、CPU时间、文件句柄等。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040001','2023-07-29 15:40:21','2023-07-30 16:08:15'),(3,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;font-size:32px;&quot;&gt;&amp;nbsp;钱东：52 + 30 = 82&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;__shell___&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;___内核___&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;___配置文件_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_编辑__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/__passwd____&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;__字母____&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;___fork___&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;__top____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;__mkdir____&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;___rm___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;_字节_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;___pwd___&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__cd____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__zip____&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_untar__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;__echo____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;__./__&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;__&amp;amp;____&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;___|___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;_which____&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_help_&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;_ls -al_____&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;__ls____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;__find____&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__chmod____&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;__chown____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;_date_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;__ps____&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;__kill____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;__cp____&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;_mv_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#111827;font-family:&amp;quot;font-size:14px;font-weight:600;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;lt;&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;_scp_____&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;_scp_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;__echo 和重定向 &amp;gt;&amp;gt;____&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;__sed____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;__mount____&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是 unmount&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;_passwd_____&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;_cat/etc/passwd_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;_grep_____&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;_grep_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;$RANDOM_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;__env____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__tar____&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;_tar_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux是一种开源的、类Unix的操作系统内核&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;1991年&lt;/span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;就是Linux内核的诞生&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; LINUX所有人可维护 稳定性高 性能好&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;文件（内容 属性）让文件认用户&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	权限修改方式有2种 1：数字形式&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2：字母形式&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Vim（Vi IMproved）是一款功能强大的文本编辑器，是 Unix 系统中的经典编辑器之一。它是 Vi 编辑器的增强版本，提供了丰富的功能和灵活的配置选项，适用于编辑各种类型的文本文件。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 模式：Vim 在编辑文本时采用多种模式，包括普通模式（Normal Mode）、插入模式（Insert Mode）、命令行模式（Command-Line Mode）等。在普通模式下，可以执行文本编辑命令；切换到插入模式后，可以像普通文本编辑器一样输入文本；在命令行模式下，可以执行复杂的编辑操作和命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;&quot;&gt;进程和程序的关系：&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;&quot;&gt;程序（Program）是一组指令的有序集合，它是静态的，通常保存在磁盘或其他存储介质中。程序本身并没有执行的能力，它只是一个文件，描述了如何执行特定的任务。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;&quot;&gt;进程（Process）是程序在执行过程中的实体。当程序被加载到内存并开始执行时，操作系统会为其创建一个进程，使得程序的指令能够被CPU执行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;&quot;&gt;&#xa;	简而言之，程序是一系列指令的代码，而进程是这些指令在执行过程中的实例&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程的管理：&#xa;操作系统负责管理计算机的资源，并确保各个进程能够合理地共享这些资源。进程管理的主要任务包括：&#xa;		&lt;/p&gt;&#xa;		&lt;ul&gt;&#xa;			&lt;li&gt;&#xa;				创建进程：当用户启动一个程序时，操作系统会创建一个新的进程来执行该程序。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				终止进程：当程序执行完成或发生错误时，操作系统会终止相应的进程，释放其占用的资源。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				进程调度：操作系统通过调度算法决定哪个进程获得CPU的执行时间，以确保公平性和高效性。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				进程间通信：不同&#xa;			&lt;/li&gt;&#xa;		&lt;/ul&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;br /&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040002','2023-07-29 15:40:21','2023-07-30 16:10:16'),(4,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;&lt;span style=&quot;color:#555555;font-family:fzzh, Helvetica, Arial, sans-serif;font-size:32px;background-color:#F2F2F2;&quot;&gt;崔明赫：58 + 25 = 83&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;shell__&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;内核&lt;/strong&gt;_____&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;配置文件_&lt;/strong&gt;_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_末行&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;模式。&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/_&lt;strong&gt;passwd&lt;/strong&gt;_&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_符号_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_fork_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_top&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_mkdir_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;rm&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_&lt;/strong&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;strong&gt;bytes&lt;/strong&gt;&lt;/span&gt;&lt;strong&gt;&lt;/strong&gt;&lt;strong&gt;_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;__&lt;strong&gt;_pwd_&lt;/strong&gt;__&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_cd&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_zip_&lt;/strong&gt;&lt;strong&gt;&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_untar&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_cat&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;echo__&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;_&lt;strong&gt;_”+x“__&lt;/strong&gt;__&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;_&lt;strong&gt;nohup  [命令行]  &amp;amp;__&lt;/strong&gt;___&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;_&lt;strong&gt;_&quot; | “_&lt;/strong&gt;___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_which___&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;man____&lt;/strong&gt;&lt;/span&gt;&lt;span&gt;&lt;strong&gt;。&lt;/strong&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;__ls -l_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_ls_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;___&lt;strong&gt;find_&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_grep&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;chmod&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_chown__&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_date___&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;___&lt;strong&gt;ps__&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_kill_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_cp&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;mv&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_cat____&lt;/strong&gt;&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_&amp;gt;_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;scp__&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_scp_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_echo&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;___&lt;strong&gt;sed&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_mount_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是&lt;strong&gt; umount&lt;/strong&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_cat a.b.c.&amp;gt;f&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;grep_&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_passwd__&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_who&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;grep 指定文件名&lt;/strong&gt;____&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;grep -r /-R__&lt;/strong&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_export_&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;env____&lt;/strong&gt;&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__&lt;strong&gt;_tar -c__&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;_&lt;strong&gt;_tar -xzf___&lt;/strong&gt;_&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;答：Linux 的历史&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux是一种自由和开放源代码的类Unix操作系统内核。它的历史可以追溯到1991年，由芬兰大学生林纳斯·托瓦兹（Linus Torvalds）开始开发。当时，他为了得到一台个人计算机，购买了一台Intel 80386处理器，并希望能在上面运行Minix操作系统。但是，由于Minix的许可限制，他决定自己编写一个类似的内核，并把它命名为Linux，将其释出成开放源代码。随后，全球范围的开发者开始为Linux贡献代码，使其成为一个拥有强大功能和稳定性的操作系统。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt; &lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	系统的特点包括：&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;开源和自由：&lt;/span&gt; Linux是自由软件，用户可以免费获取、使用和分发它。同时，其源代码对所有人开放，任何人都可以查看、修改和改进代码。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;多用户和多任务：&lt;/span&gt; Linux支持多用户的同时登录，并且可以同时运行多个任务或进程，使其成为一个强大的服务器操作系统。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;稳定性和可靠性：&lt;/span&gt; Linux内核和大部分软件组件经过长时间的测试和使用，具有很高的稳定性和可靠性。许多服务器和关键系统选择Linux操作系统，以确保长时间的稳定运行。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;良好的性能：&lt;/span&gt; Linux系统优化了资源的使用，使得它在各种硬件上运行效率较高，可以运行在较低配置的计算机上。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;强大的命令行：&lt;/span&gt; Linux提供了强大的命令行界面，称为Shell，使得用户可以通过命令行进行各种任务和管理操作，提供了更大的自由度和控制权。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;广泛的软件支持：&lt;/span&gt; Linux拥有丰富的开源软件库，提供了各种类型的应用程序和工具，涵盖了几乎所有的领域，满足了不同用户的需求。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;可定制性：&lt;/span&gt; 由于Linux是开源的，用户可以根据自己的需求自由定制和配置系统，使其适应不同的场景和用途。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;br /&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;答：&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	文件权限的含义如下：&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;读取权限（r）：&lt;/span&gt; 允许用户查看文件内容、目录中的文件列表或链接文件的目标。对于目录，读取权限允许用户列出目录中的内容。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;写入权限（w）：&lt;/span&gt; 允许用户修改文件内容、创建或删除文件，对于目录，写入权限允许用户在目录中创建、删除或重命名文件。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			&lt;span style=&quot;font-weight:600;color:var(--tw-prose-bold);&quot;&gt;执行权限（x）：&lt;/span&gt; 对于普通文件，执行权限允许用户作为可执行程序运行文件。对于目录，执行权限允许用户进入目录。&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 权限用字符表示，分别为r（读取）、w（写入）和x（执行），用三个字符表示一组权限，例如rw-r--r--表示所有者有读写权限，群组和其他用户只有读取权限。&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 修改文件权限，可以使用chmod命令。chmod命令通过使用不同的权限数字或符号来更改文件权限。&#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp;答：&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Vim是一款经典的文本编辑器，它是Unix和类Unix系统中广泛使用的编辑器之一。Vim最初由Bram Moolenaar于1991年发布，它是Vi编辑器的增强版。&quot;Vim&quot;是&quot;Vi&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;IMproved&quot;的缩写。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;以下是Vim编辑器的一些特点和功能：&lt;br /&gt;&#xa;模态编辑：Vim有不同的编辑模式，最常用的是命令模式和插入模式。在命令模式下，用户可以使用按键命令进行光标移动、复制、粘贴、搜索等操作。而在插入模式下，用户可以像普通编辑器一样输入文本。&lt;br /&gt;&#xa;快速高效：Vim强调使用键盘快捷键和命令来编辑文本，这使得熟练的Vim用户可以非常高效地编辑大量的文本。&lt;br /&gt;&#xa;可定制性：Vim具有广泛的可定制性。用户可以通过编辑配置文件来调整编辑器的外观和行为，也可以安装插件来扩展功能。&lt;br /&gt;&#xa;跨平台支持：Vim可在多种操作系统上运行，包括Linux、macOS、Windows等。&lt;br /&gt;&#xa;强大的搜索和替换功能：Vim支持正则表达式搜索和批量替换，使得在文本中查找和修改特定模式的内容变得简单。&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;答：关系&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;程序是静态的代码和数据，而进程是程序的动态执行实例。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;进程管理使得操作系统可以高效地同时运行多个程序，实现了多任务处理和资源隔离，从而提高了计算机的利用率和整体性能。&lt;/span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040003','2023-07-29 15:40:21','2023-07-30 16:12:06'),(5,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;font-size:32px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#555555;font-family:fzzh, Helvetica, Arial, sans-serif;font-size:32px;background-color:#FFFFFF;&quot;&gt;刘龙：50 + 20 = 70&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;__shell外壳____&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;_内核_____&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;___配置文件_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;__可视____&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/_passwd_____&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;__符号____&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;__fork____&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;__top____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;__mkdir____&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;__rm -rf____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;__字节（byte）___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;_pwd_____&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__cd____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__zip___&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__tar____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;__echo____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;_./_____&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;___&amp;amp;___&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;__|___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;__which____&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;___man___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;__ls -al___&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;__ls____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;___find___&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__chmod___&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;__chown___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;__date___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;__ps____&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;_kill____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;___cp___&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;__mv____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_cat_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;___&amp;gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;___scp___&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;___scp&lt;/span&gt;&lt;span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_echo_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;__sed____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;___mount___&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是umount&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;background-color:#E53333;&quot;&gt;cat aa&amp;nbsp; &amp;gt;&amp;nbsp; &amp;nbsp;bb_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_grep&amp;nbsp; &amp;nbsp;**&amp;nbsp; &amp;nbsp; &amp;nbsp;/**_&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;_passwd&amp;nbsp; (用户名)_____&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;_whoami_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;background-color:#E53333;&quot;&gt;grep&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;_export_____&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;___env___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;_tar____&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;__tar -zxf____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux是一种自由、开源的操作系统内核，诞生于1991年。它的发展始于芬兰计算机科学家Linus Torvalds（林纳斯·托瓦兹）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;开发的一个小的内核原型。&lt;/span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;随着全球开发者的参与和贡献，Linux逐渐演化成一个完整的操作系统，成为最受欢迎和广泛使用&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;的开源操作系统之一。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux操作系统的特点包括：&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			自由和开源&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			多用户和多任务&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			支持广泛的硬件平&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			强大的命令行工具服务&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			模块化设计&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			稳定性和安全性&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;在Linux系统中，每个文件和目录都有三组权限：所有者权限、用户组权限和其他用户权限。这些权限规定了谁可以对文件或目录进行读取、&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	写入和执行操作。权限用字符表示，一般由三个字符组成：r（读取权限）、w（写入权限）和x（执行权限）。&#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;span style=&quot;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;span style=&quot;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;要修改文件权限，可以使用&lt;/span&gt;chmod&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;命令。&lt;/span&gt;chmod&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;命令允许用户更改文件或目录的权限。它可以使用数字表示法或符号表示法来指定权限。&lt;/span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	Vim）是一个文本编辑器，是Linux和类Unix系统中最受欢迎的编辑器之一。它是Unix系统中的vi编辑器的增强版本，&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	由Bram Moolenaar于1991年发布。Vim具有强大的编辑功能和高度可定制性，被广泛用于程序开发、系统管理和文本编辑等任务。&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	Vim编辑器的特点和功能包括：&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			模式&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			快捷键&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			多窗口编辑&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			插件和脚本&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			强大的搜索和替换&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			高度可定制&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p&gt;&#xa;	&lt;span&gt;&amp;nbsp;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;关系：程序是静态的代码集合，而进程是程序在内存中的实际执行实例。一个程序可以同时创建多个进程，每个进程都有自己独立的内存空间、数据、状态和执行上下文。&lt;/span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	以下是管理进程的一些关键方面：&#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程创建&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程调度&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程终止&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程通信&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程同步&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			进程优先级&#xa;		&lt;/p&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040004','2023-07-29 15:40:21','2023-07-30 16:04:44'),(6,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;&lt;span style=&quot;font-size:32px;&quot;&gt;&lt;/span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;strong&gt;&lt;span style=&quot;font-size:24px;&quot;&gt;&amp;nbsp;欧孝天：55 + 25 = 80&lt;/span&gt;&lt;/strong&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空1&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_shell&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_内核_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;配置文件&lt;/span&gt;_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;末行&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/__&lt;span style=&quot;color:#E53333;&quot;&gt;passwd_&lt;/span&gt;___&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;符号_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_fork&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_top_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_mkdir__&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;rm -rf_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_Byte字节&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_pwd__&lt;/span&gt;_&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;cd xx__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;gzip__&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;zip_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;cat_&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;_&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;_&lt;/span&gt;&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_echo&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;__./___&lt;/span&gt;_&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;____&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;amp;&lt;/span&gt;__&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_|_&lt;/span&gt;__&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;which_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;____&lt;span style=&quot;color:#E53333;&quot;&gt;--help_&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;ls -l_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;ls_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;_find_&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_grep&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_chmod_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;__chown_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;date_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_ps__&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;__kill_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;cp&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;mv&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;cat_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;gt;_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_scp_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_scp_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;echo&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_sed&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;_mount&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是 &lt;span style=&quot;color:#E53333;&quot;&gt;umont&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;cat a. b. c. &amp;gt; f.__&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;_grep&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;___&lt;span style=&quot;color:#E53333;&quot;&gt;passwod&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;__who&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;在Linux系统中，用于在文件中搜索指定字符串的命令是_&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;_&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;font-size:13px;background-color:#FFFFFF;color:#E53333;&quot;&gt;grep&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;_&quot;指定&quot; 文件名&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;_&lt;/span&gt;__，用于在多个文件中搜索指定字符串的命令是___&lt;span&gt;&lt;span style=&quot;font-size:13px;background-color:#FFFFFF;color:#E53333;&quot;&gt;grep&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#E53333;&quot;&gt;_&quot;指定&quot; *文件名&lt;/span&gt;___。&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;在Linux系统中，用于设置环境变量的命令是_&lt;span style=&quot;color:#E53333;&quot;&gt;_&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;export&lt;/span&gt;___，用于查看当前已经设置的环境变量的命令是___&lt;span style=&quot;color:#E53333;&quot;&gt;env&lt;/span&gt;___。&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__&lt;span style=&quot;color:#E53333;&quot;&gt;_tar -c_&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;color:#E53333;&quot;&gt;__tar -xzf__&lt;/span&gt;&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&lt;span style=&quot;color:#E53333;&quot;&gt; 答：&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;历史：&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFDD29;&quot;&gt;linux操作系统的起源可以追溯到1991年，由芬兰计算机科学家Linus Torvalds创建。他最初是为了满足个人计算机的需求而开发了一个基于Minix操作系统的内核。随着时间的推移，Linux内核开始吸引了许多开发者的兴趣，并形成了一个庞大的开源社区。这个社区为Linux内核的发展做出了巨大贡献，不断完善和扩展了Linux操作系统。&lt;/span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;nbsp; &amp;nbsp;特点&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;nbsp; &amp;nbsp;特点：&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;1.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;开源性质：Linux是一个完全开源的操作系统。这意味着任何人都可以查看、修改和分发Linux的源代码。这种开源性带来了灵活性、可定制性和透明性，使得Linux能够适应各种不同的需求。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;2.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;多用户和多任务：Linux是一个多用户和多任务的操作系统。多用户意味着多个用户可以同时登录和使用系统。多任务表示系统可以同时运行多个程序，每个程序都在自己的进程中运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;3.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;强大的稳定性和可靠性：Linux被广泛认为是一个非常稳定和可靠的操作系统。它的设计哲学注重开发稳定性、安全性和可靠性。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;4.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;强大的网络功能：Linux操作系统是网络友好的，提供了广泛且强大的网络功能和工具。它支持网络协议、服务和应用程序，并提供了强大的网络管理和配置工具。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;5.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;丰富的软件生态系统：Linux拥有一个庞大而活跃的开源社区，这个社区为Linux提供了大量的自由开源软件。用户可以从软件包管理器中轻松获取并安装各种应用程序和工具。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;6.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;跨平台兼容性：Linux内核可以运行在各种硬件体系结构上，从个人电脑到服务器、嵌入式设备、移动设备等。这种跨平台兼容性使得Linux成为一个广泛适用的操作系统。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &lt;span style=&quot;color:#E53333;&quot;&gt;答：在linux&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;文件权限决定了对文件或目录的访问权限。它由三组权限组成：所有者权限、群组权限和其他用户权限。&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;每组权限包含三个字符：读取权限（r），写入权限（w）和执行权限（x）&amp;nbsp;&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;例如：有一个cv的目录，如果修改放开文件权限，可以使用chmod命令，语法是【chmod 777 cv 】&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &lt;span style=&quot;color:#E53333;&quot;&gt;答：&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;它以高度可定制性和强大的编辑功能而闻名，并广泛用于程序开发、系统管理和文本编辑等领域。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;特点：&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;1.Vim有多种编辑模式，包括命令模式、插入模式、可视模式等。在命令模式下可以执行编辑和操作命令；在插入模式下可以输入文本；在可视模式下可以进行选中和操作&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;nbsp; 2.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;Vim提供了丰富的编辑功能，包括多级撤销、搜索和替换、查找括号匹配、自动补全、宏录制等。它还支持多个窗口和标签页，可以同时编辑和查看多个文件。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt;&amp;nbsp; &amp;nbsp;3.&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;Vim拥有一个庞大而活跃的插件社区，提供了各种插件和脚本扩展，丰富了编辑器的功能和可扩展性。用户可以根据需要安装和使用各种插件来增强编辑器的特性&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;span style=&quot;color:#E53333;&quot;&gt; &amp;nbsp;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&lt;span style=&quot;color:#E53333;&quot;&gt;答：&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;程序是静态的，是存在于磁盘中的文件；&lt;/span&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;进程是程序的执行实例，是活动中的实体&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:rgba(35, 41, 56, 0.8);font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#E53333;font-family:&amp;quot;font-size:13px;background-color:#FFFFFF;&quot;&gt;管理进程的工具和接口可以通过命令行工具（如ps、kill、top）&lt;/span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040005','2023-07-29 15:40:21','2023-07-30 16:00:47'),(7,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;font-size:32px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#555555;font-family:fzzh, Helvetica, Arial, sans-serif;font-size:32px;background-color:#FFFFFF;&quot;&gt;吴彦祺：52 + 30 = 82&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align:left;margin-left:36pt;text-indent:-18pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;_shell_&lt;strong&gt;&lt;/strong&gt;___&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;_内核_____&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;_系统配置文件_______&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_编辑____&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/_passwd_____&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;_符号_____&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;_fork__&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;__top____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;_mkdir_____&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;_rm_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;__字节____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;_pwd__&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__cd____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;_gzip__&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;__gunzip____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;__cat__&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;__echo__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;在文件名后加上&lt;span style=&quot;background-color:#E53333;&quot;&gt;__x___&lt;/span&gt;_表示执行该文件，而不是在终端中输入命令。&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;__&amp;amp;___&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;___|___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;__which__&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;__man__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;_ls -al__&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;__ls____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;__find___&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep_&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;color:#E53333;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;&lt;/span&gt;&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__chmod___&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;__chown___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;__date____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;__ps aux__&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_kall_&lt;/span&gt;___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;__cp___&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;__mv____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;__&amp;gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;__scp__&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;__scp____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;__echo___&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;__sed____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;__mount____&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是 umount&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;__cat__&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;__grep__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;在Linux系统中，用于修改用户密码的命令是__passwd__，用于查看当前登录用户的身份信息的命令是__whoami____。&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep___&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;__export___&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;printenv_&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__tar_&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;__tar___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;特点&amp;nbsp; :&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:var(--tw-prose-bold);font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;开源和自由：&lt;/span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt; Linux是开源的，任何人都可以自由地访问、使用、修改和分发Linux的源代码。&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 这使得Linux成为一个自由、透明和充满活力的操作系统&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;span style=&quot;color:var(--tw-prose-bold);font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;命令行和脚本支持&lt;strong&gt;：&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt; Linux系统提供强大的命令行接口，使用户可以通过命令行执行各种操作。&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 这对于系统管理、自动化任务和脚本编程非常有用，提高了工作效率&lt;/span&gt;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;历史&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp;Linux的历史可以追溯到1991年，由芬兰大学生林纳斯·托瓦兹（Linus Torvalds）创造。他开始开发&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;一个基于Minix的操作系统内核，以填补当时商业操作系统昂贵的空白。他发布了第一个Linux内核版&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;本（0.01版）并向公众开放，从此Linux社区逐渐形成。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;Linux得名于创始人林纳斯·托瓦兹的名字和操作系统Unix的结合。虽然内核是Linux最重要的组成部分，&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;但实际上Linux通常指的是完整的操作系统，包括内核和相关的系统工具、库、应用程序等。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;ol style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&#xa;	&lt;li&gt;&#xa;		&lt;p&gt;&#xa;			属主权限（user）：表示文件或目录的所有者对其具有的权限。&#xa;		&lt;/p&gt;&#xa;		&lt;ul&gt;&#xa;			&lt;li&gt;&#xa;				读（r）： 可以查看文件内容和目录列表。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				写（w）：可以修改文件内容和目录中的文件名。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				执行（x）：对于文件，表示可以执行它作为可执行文件；对于目录，表示可以进入其中。&#xa;			&lt;/li&gt;&#xa;			&lt;li&gt;&#xa;				&lt;br /&gt;&#xa;			&lt;/li&gt;&#xa;		&lt;/ul&gt;&#xa;	&lt;/li&gt;&#xa;&lt;/ol&gt;&#xa;属组和其他人的权限和属主一样&lt;br /&gt;&#xa;&lt;p&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;修改权限 ： 可以使用chmod, 可以用两种方法来修改&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align:justify;margin-left:36pt;text-indent:-18pt;&quot;&gt;&#xa;	&lt;span style=&quot;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;1 数字表示法&amp;nbsp; r=4 w=2 x=1&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align:justify;margin-left:36pt;text-indent:-18pt;&quot;&gt;&#xa;	&lt;span style=&quot;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2 符号表示法&amp;nbsp; u代表属主 g代表属组 o代表其他人&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;&lt;span&gt;Vim&lt;/span&gt;&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	1 ： vim是一种强大的文本编辑器&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	2 ： vim的模式 第一种：编辑模式&amp;nbsp; 可以输入和编辑文本内容&amp;nbsp; &amp;nbsp;第二种：命令模式&amp;nbsp; 执行各种操作 比如 复制 粘贴 删除 等&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;第三种 插入模式 可以在这个模式里输入文本 按i进入 按esc退出&amp;nbsp; &amp;nbsp;第四种&amp;nbsp; 末行模式&amp;nbsp; 可以在编辑完文本号&amp;nbsp; 输入保存&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 退出&amp;nbsp; 按下 “：” 便可以输入&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	3 ： vim的快捷键&amp;nbsp; &amp;nbsp; 1，dd 删除光标所在行&amp;nbsp; 2，yy复制光标所在行&amp;nbsp; 3，p，粘贴所复制的文本&amp;nbsp; 4，w 保存文本&amp;nbsp; 5，q 退出&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;器&amp;nbsp; 6，wq 保存并退出编辑器&amp;nbsp; &amp;nbsp;7，q!&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;强制退出编辑器不保存修改&amp;nbsp;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;进程和程序的关系 ：&amp;nbsp;&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;进程是指程序在计算机系统中的执行实例。当程序被执行时，操作系统会为其创建一个独立的进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp;进程是动态的，具有运行状态和资源占用，它在计算机的内存中运行，并拥有自己的程序计数器、堆栈、数据段、代码段&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;color:#374151;font-family:Söhne, ui-sans-serif, system-ui, -apple-system, &amp;quot;font-size:16px;background-color:rgba(247, 247, 248, 0.5);&quot;&gt;&amp;nbsp;等资源。一个程序可以同时创建多个进程的实例，这些进程之间相互独立，每个进程运行在自己的虚拟空间内&lt;/span&gt; &#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040006','2023-07-29 15:40:21','2023-07-30 16:14:05'),(8,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span style=&quot;font-size:32px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#555555;font-family:fzzh, Helvetica, Arial, sans-serif;font-size:32px;background-color:#F2F2F2;&quot;&gt;边志强：52 + 25 = 77&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align:left;margin-left:18pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;__shell外壳____&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;___内壳___&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;___配置文件_______&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;Vim&lt;span&gt;编辑器有三种模式，分别是命令模式、插入模式和&lt;/span&gt;&lt;span&gt;___末行___&lt;/span&gt;&lt;span&gt;模式。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;用户管理包括用户和用户组信息的维护，每个用户只能属于一个主组，但可以加入多个辅助组。这些信息存储在&lt;/span&gt;/etc/___passwd___&lt;span&gt;文件中。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	5.&amp;nbsp;&lt;span&gt;文件的三组权限包括所有者权限、用户组权限和其他用户权限，分别用&lt;/span&gt;r&lt;span&gt;、&lt;/span&gt;&lt;span&gt;w&lt;/span&gt;&lt;span&gt;、&lt;/span&gt;&lt;span&gt;x&lt;/span&gt;&lt;span&gt;表示。更改权限的两种方法分别是数字表示法和&lt;/span&gt;&lt;span&gt;__符号____&lt;/span&gt;&lt;span&gt;表示法。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	6.&amp;nbsp;&lt;span&gt;进程是程序执行的实例，具有&lt;/span&gt;PID&lt;span&gt;以及与之相关联的其他信息。通过&lt;/span&gt;&lt;span&gt;____fork____&lt;/span&gt;&lt;span&gt;命令可以创建新任务，通过&lt;/span&gt;&lt;span&gt;___top____&lt;/span&gt;&lt;span&gt;命令可以查看系统资源消耗。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	7.&amp;nbsp;Linux&lt;span&gt;系统中，用于创建目录的命令是&lt;/span&gt;&lt;span&gt;____mkdir______&lt;/span&gt;&lt;span&gt;，用于删除目录的命令是&lt;/span&gt;&lt;span&gt;__rm -rf_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	8.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，文件的大小单位是&lt;/span&gt;&lt;span&gt;__Byte字节____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	9.&amp;nbsp;&lt;span&gt;查看当前工作目录的命令是&lt;/span&gt;__pwd____&lt;span&gt;，切换工作目录的命令是&lt;/span&gt;&lt;span&gt;__cd xx____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	10.&amp;nbsp;&lt;span&gt;压缩文件的常用命令有&lt;/span&gt;tar&lt;span&gt;和&lt;/span&gt;&lt;span&gt;_gzip_____&lt;/span&gt;&lt;span&gt;，解压缩文件的常用命令有&lt;/span&gt;&lt;span&gt;unzip&lt;/span&gt;&lt;span&gt;和&lt;/span&gt;&lt;span&gt;____zip__&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	11.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示文本文件内容的命令是&lt;/span&gt;&lt;span&gt;__cat____&lt;/span&gt;&lt;span&gt;，用于在终端输出信息的命令是&lt;/span&gt;&lt;span&gt;_echo_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	12.&amp;nbsp;&lt;span&gt;在文件名后加上&lt;/span&gt;__./____&lt;span&gt;表示执行该文件，而不是在终端中输入命令。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	13.&amp;nbsp;&lt;span&gt;执行一条长时间运行的命令时，可以使用&lt;/span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;_$___&lt;/span&gt;_&lt;span&gt;符号将其放入后台运行。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	14.&amp;nbsp;&lt;span&gt;将一个命令的输出作为另一个命令的输入可以使用&lt;/span&gt;___|___&lt;span&gt;符号。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	15.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查询命令所在位置的命令是&lt;/span&gt;&lt;span&gt;__which____&lt;/span&gt;&lt;span&gt;，用于查询命令的帮助信息的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;__--help___&lt;/span&gt;_&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	16.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于列出文件和目录详细信息的命令是&lt;/span&gt;&lt;span&gt;___ls -l______&lt;/span&gt;&lt;span&gt;，用于只列出文件和目录名称的命令是&lt;/span&gt;&lt;span&gt;____ls___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	17.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于查找文件的命令是&lt;/span&gt;&lt;span&gt;__find______&lt;/span&gt;&lt;span&gt;，用于查找指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep_____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	18.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改文件权限的命令是&lt;/span&gt;&lt;span&gt;__chmod_____&lt;/span&gt;&lt;span&gt;，用于修改文件所有者的命令是&lt;/span&gt;&lt;span&gt;___chown___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	19.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示当前系统时间的命令是&lt;/span&gt;&lt;span&gt;__date____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	20.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于显示正在运行的进程信息的命令是&lt;/span&gt;&lt;span&gt;__ps____&lt;/span&gt;&lt;span&gt;，用于杀死一个进程的命令是&lt;/span&gt;&lt;span&gt;___kill____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	21.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于复制文件或目录的命令是&lt;/span&gt;&lt;span&gt;___cp_____&lt;/span&gt;&lt;span&gt;，用于移动文件或目录的命令是&lt;/span&gt;&lt;span&gt;__mv____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	22.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打印文件内容到标准输出设备的命令是&lt;/span&gt;&lt;span&gt;___cat_____&lt;/span&gt;&lt;span&gt;，用于将标准输入设备重定向到指定文件的命令是&lt;/span&gt;&lt;span&gt;__&amp;gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	23.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将文件或目录从本地复制到远程主机的命令是&lt;/span&gt;&lt;span&gt;___scp___&lt;/span&gt;&lt;span&gt;，用于从远程主机复制文件或目录到本地的命令是&lt;/span&gt;&lt;span&gt;__scp____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	24.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中添加新内容的命令是&lt;/span&gt;&lt;span&gt;__echo____&lt;/span&gt;&lt;span&gt;，用于用指定字符串替换文件中的某个字符串的命令是&lt;/span&gt;&lt;span&gt;__sed____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	25.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于挂载文件系统的命令是&lt;/span&gt;&lt;span&gt;__mount____&lt;/span&gt;&lt;span&gt;，用于卸载文件系统的命令是&amp;nbsp; umont&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	26.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于将多个文件合并成一个文件的命令是&lt;/span&gt;&lt;span&gt;__cat a.b.c.&amp;gt;f.____&lt;/span&gt;&lt;span&gt;，用于从文件中提取部分内容的命令是&lt;/span&gt;&lt;span&gt;__cat v.txt | head -n 1____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	27.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于修改用户密码的命令是&lt;/span&gt;&lt;span&gt;_&lt;span style=&quot;background-color:#E53333;&quot;&gt;passwod&lt;/span&gt;_____&lt;/span&gt;&lt;span&gt;，用于查看当前登录用户的身份信息的命令是&lt;/span&gt;&lt;span&gt;___who___&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	28.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于在文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__grep &quot;指定&quot; 文件名____&lt;/span&gt;&lt;span&gt;，用于在多个文件中搜索指定字符串的命令是&lt;/span&gt;&lt;span&gt;__&lt;span&gt;grep &quot;指定&quot; *文件名__&lt;/span&gt;____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	29.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于设置环境变量的命令是&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;background-color:#E53333;&quot;&gt;_xx=xx___&lt;/span&gt;__&lt;/span&gt;&lt;span&gt;，用于查看当前已经设置的环境变量的命令是&lt;/span&gt;&lt;span&gt;__env____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	30.&amp;nbsp;&lt;span&gt;在&lt;/span&gt;Linux&lt;span&gt;系统中，用于打包文件的命令是&lt;/span&gt;&lt;span&gt;__tar -c____&lt;/span&gt;&lt;span&gt;，用于解包文件的命令是&lt;/span&gt;&lt;span&gt;__tar -xzf____&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;二、简答题（每题&lt;/span&gt;&lt;span&gt;10&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;40&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Linux&lt;span&gt;系统的历史和特点。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&amp;nbsp;历史： linux操作系统的起源可以追溯到1991年，由芬兰计算机科学家Linus Torvalds创建。他最初是为了满足个人计算机的需求而开发了一个基于Minix操作系统的内核。随着时间的推移，Linux内核开始吸引了许多开发者的兴趣，并形成了一个庞大的开源社区。这个社区为Linux内核的发展做出了巨大贡献，不断完善和扩展了Linux操作系统。&amp;nbsp; &amp;nbsp;特点&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&amp;nbsp; &amp;nbsp;特点：&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;1.开源性质：Linux是一个完全开源的操作系统。这意味着任何人都可以查看、修改和分发Linux的源代码。这种开源性带来了灵活性、可定制性和透明性，使得Linux能够适应各种不同的需求。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;2.多用户和多任务：Linux是一个多用户和多任务的操作系统。多用户意味着多个用户可以同时登录和使用系统。多任务表示系统可以同时运行多个程序，每个程序都在自己的进程中运行。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;3.强大的稳定性和可靠性：Linux被广泛认为是一个非常稳定和可靠的操作系统。它的设计哲学注重开发稳定性、安全性和可靠性。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;4.强大的网络功能：Linux操作系统是网络友好的，提供了广泛且强大的网络功能和工具。它支持网络协议、服务和应用程序，并提供了强大的网络管理和配置工具。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;5.丰富的软件生态系统：Linux拥有一个庞大而活跃的开源社区，这个社区为Linux提供了大量的自由开源软件。用户可以从软件包管理器中轻松获取并安装各种应用程序和工具。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;6.跨平台兼容性：Linux内核可以运行在各种硬件体系结构上，从个人电脑到服务器、嵌入式设备、移动设备等。这种跨平台兼容性使得Linux成为一个广泛适用的操作系统&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;justify&quot; style=&quot;text-align:justify;&quot;&gt;&#xa;	&lt;br /&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;&lt;span&gt;请解释一下&lt;/span&gt;Linux&lt;span&gt;系统中文件权限的含义及如何修改文件权限。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;在linux文件权限决定了对文件或目录的访问权限。它由三组权限组成：所有者权限、群组权限和其他用户权限。每组权限包含三个字符：读取权限（r），写入权限（w）和执行权限（x）&amp;nbsp;&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;例如：有一个cv的目录，如果修改放开文件权限，可以使用chmod命令，语法是【chmod 777 cv 】&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&amp;nbsp;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	3.&amp;nbsp;&lt;span&gt;请简要介绍&lt;/span&gt;Vim&lt;span&gt;编辑器。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;它以高度可定制性和强大的编辑功能而闻名，并广泛用于程序开发、系统管理和文本编辑等领域。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;特点：&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;1.Vim有多种编辑模式，包括命令模式、插入模式、可视模式等。在命令模式下可以执行编辑和操作命令；在插入模式下可以输入文本；在可视模式下可以进行选中和操作&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&amp;nbsp; 2.Vim提供了丰富的编辑功能，包括多级撤销、搜索和替换、查找括号匹配、自动补全、宏录制等。它还支持多个窗口和标签页，可以同时编辑和查看多个文件。&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&amp;nbsp; &amp;nbsp;3.Vim拥有一个庞大而活跃的插件社区，提供了各种插件和脚本扩展，丰富了编辑器的功能和可扩展性。用户可以根据需要安装和使用各种插件来增强编辑器的特性&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	4.&amp;nbsp;&lt;span&gt;请简要介绍进程和程序的关系以及如何管理进程。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;程序是静态的，是存在于磁盘中的文件；进程是程序的执行实例，是活动中的实体&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;管理进程的工具和接口可以通过命令行工具（如ps、kill、top）&lt;br /&gt;&#xa;&lt;br /&gt;&#xa;&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;&#xa;&lt;/p&gt;','2304Linux系统管理-考试',0,0,'jx23040007','2023-07-29 15:40:21','2023-07-30 16:15:48'),(9,'2304Linux系统管理-考试','&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;b&gt;&lt;span&gt;觉行学校&lt;/span&gt;&lt;span&gt;2304&lt;/span&gt;&lt;span&gt;班&lt;/span&gt;&lt;span&gt;Linux&lt;/span&gt;&lt;span&gt;系统管理试题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;/b&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;margin-left:18.0000pt;text-align:center;&quot;&gt;&#xa;	&lt;span&gt;2023&lt;/span&gt;&lt;span&gt;年&lt;/span&gt;&lt;span&gt;7&lt;/span&gt;&lt;span&gt;月&lt;/span&gt;&lt;span&gt;28&lt;/span&gt;&lt;span&gt;日&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&amp;nbsp;&lt;span style=&quot;color:#555555;font-family:fzzh, Helvetica, Arial, sans-serif;font-size:32px;background-color:#FFFFFF;&quot;&gt;李金辉：0&lt;/span&gt;&#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:18.0000pt;&quot;&gt;&#xa;	&lt;span&gt;一、填空题（每空&lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;分，共&lt;/span&gt;&lt;span&gt;60&lt;/span&gt;&lt;span&gt;分）&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	1.&amp;nbsp;Linux&lt;span&gt;系统将用户的意愿转化为命令交给&lt;/span&gt;&lt;span&gt;______&lt;/span&gt;&lt;span&gt;，&lt;/span&gt;&lt;span&gt;shell&lt;/span&gt;&lt;span&gt;根据命令在&lt;/span&gt;&lt;span&gt;______&lt;/span&gt;&lt;span&gt;中创建进程操作输入输出设备。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left:36.0000pt;text-indent:-18.0000pt;&quot;&gt;&#xa;	2.&amp;nbsp;Linux&lt;span&gt;目录结构中，&lt;/span&gt;&lt;span&gt;/etc&lt;/span&gt;&lt;span&gt;目录用于存放&lt;/span&gt;&lt;span&gt;________&lt;/span&gt;&lt;span&gt;。&lt;/span&gt; &#xa;&lt;/p&gt;&#xa;&lt